import { Store } from '@ngrx/store';
import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { firstValueFrom, Observable } from 'rxjs';
import { login, logout } from 'src/app/state/auth/auth.action';
import { selectAuthUser } from 'src/app/state/auth/auth.selector';
import { environment } from 'src/environments/environment';
import { reset, update } from 'src/app/state/favorites/favoritesd.action';
import { IAppState } from 'src/app/state/app/app.type';


@Injectable({
  providedIn: 'root'
})
export class AuthenticationService {

  //#region Lifecycle

  constructor(
    private http: HttpClient,
    private store: Store<IAppState>
  ) { }

  //#endregion

  //#region Methods

  /**
   * @description
   * Checks if user is logged in
   */
  isLoggedIn(): Observable<boolean> {
    return new Observable(observer => {
      firstValueFrom(this.store.select(selectAuthUser)).then(e => observer.next(Boolean(e?.username)));
    });
  }

  /**
   * @description
   * Logs user in
   *
   * @param username The username that wants to log in
   * @param password The password rerlated to the username
   */
  login(username: string, password: string): Promise<void> {
    return new Promise((resolve, reject) => {
      firstValueFrom(
        this.http.get<Array<any>>(`${environment.api.auth}/users?username=${username}&password=${password}`)
      ).then(data => {
        if (data.length > 0) {

          // Simulating latency for 1.2 second
          setTimeout(() => {
            const user = data[0];

            this.store.dispatch(login({ id: user.id, username: user.username }));
            this.store.dispatch(update({ favorites: user.favorites }))

            resolve();
          }, 1200);
        } else {
          reject();
        }
      });
    });
  }

  /**
   * @description
   * Logs user out
   */
  logout(): void {
    this.store.dispatch(logout());
    this.store.dispatch(reset());
  }

  //#endregion
}

import { selectAuthUserId } from 'src/app/state/auth/auth.selector';
import { environment } from 'src/environments/environment';
import { HttpClient } from '@angular/common/http';
import { Injectable } from '@angular/core';
import { firstValueFrom, forkJoin, map } from 'rxjs';
import { Store } from '@ngrx/store';
import { update } from 'src/app/state/favorites/favoritesd.action';
import { IAppState } from 'src/app/state/app/app.type';
import { Superhero } from 'src/app/models/superhero.model';
import { selectFavorites } from 'src/app/state/favorites/favorites.selector';
import { ISuperheroResponse } from 'src/app/types/superhero-response.type';


@Injectable({
  providedIn: 'root'
})
export class FavoritesService {

  //#region Lifecycle

  constructor(
    private http: HttpClient,
    private store: Store<IAppState>
  ) { }

  //#endregion

  //#region Methods

  /**
   * @description
   * Loads favorites list
   */
  loadFavorites(favorites: Array<number>): Promise<Array<Superhero>> {
    return new Promise(async (resolve, reject) => {
      firstValueFrom(
        forkJoin(
          // Generating a batch of numbers
          favorites

            // Generating api calls
            .map(e => this.http.get<ISuperheroResponse>(`${environment.api.superheroes}/${e}`))

          // Mapping the values
        ).pipe(map(e => e.map(e => new Superhero(e, true))))
      )
        .then(data => resolve(data))
        .catch(() => reject());
    });
  }

  /**
   * @description
   * Favorites a superhero
   *
   * @param id The ID of the superhero
   */
  favoriteSuperhero(id: number): Promise<void> {
    return new Promise(async (resolve, reject) => {
      const userId = await firstValueFrom(this.store.select(selectAuthUserId));
      const user = await firstValueFrom(this.http.get(`${environment.api.auth}/users/${userId}`)) as any;
      const favorites = Array.from(new Set([...user.favorites, id]));
      const newUser = { ...user, favorites }

      firstValueFrom(this.http.put<any>(`${environment.api.auth}/users/${userId}`, newUser))
        .then(() => {
          this.store.dispatch(update({ favorites }));
          resolve();
        })
        .catch(() => reject());
    })
  }

  /**
   * @description
   * Favorites a superhero
   *
   * @param id The ID of the superhero
   */
  unfavoriteSuperhero(id: number): Promise<void> {
    return new Promise(async (resolve, reject) => {
      const userId = await firstValueFrom(this.store.select(selectAuthUserId));
      const user = await firstValueFrom(this.http.get(`${environment.api.auth}/users/${userId}`)) as any;
      const favorites = Array.from(new Set([...user.favorites, id])).filter(e => e !== id);
      const newUser = { ...user, favorites }

      firstValueFrom(this.http.put<any>(`${environment.api.auth}/users/${userId}`, newUser))
        .then(() => {
          this.store.dispatch(update({ favorites }));
          resolve();
        })
        .catch(() => reject());
    })
  }

  //#endregion
}

import { HttpClient } from '@angular/common/http';
import { Injectable } from '@angular/core';
import { firstValueFrom, forkJoin, map } from 'rxjs';
import { Superhero } from 'src/app/models/superhero.model';
import { environment } from 'src/environments/environment';
import { ISuperheroResponse } from 'src/app/types/superhero-response.type';
import { ISuperheroSearchResponse } from 'src/app/types/superhero-search-response.type';
import { selectFavorites } from 'src/app/state/favorites/favorites.selector';
import { IAppState } from 'src/app/state/app/app.type';
import { Store } from '@ngrx/store';


@Injectable({
  providedIn: 'root'
})
export class SuperheroService {

  //#region Lifecycle

  constructor(
    private http: HttpClient,
    private store: Store<IAppState>
  ) { }

  //#endregion

  //#region Methods

  /**
   * @description
   * Fetches info on a particular superhero
   *
   * @param id The ID of the superhero to fetch
   */
  getSuperheroById(id: number): Promise<Superhero> {
    return new Promise((resolve, reject) => {
      firstValueFrom(this.http.get<ISuperheroResponse>(`${environment.api.superheroes}/${id}`))
        .then(data => {
          resolve(new Superhero(data));
        })
        .catch(() => reject())
    })
  }

  /**
   * @description
   * Fetches info on a particular superhero
   *
   * @param search The search query
   */
  getSuperheroBySearch(search: string): Promise<Array<Superhero>> {
    return new Promise((resolve, reject) => {
      firstValueFrom(this.http.get<ISuperheroSearchResponse>(`${environment.api.superheroes}/search/${search}`))
        .then(async data => {
          if (data.error) {
            reject();
          }

          const favorites = await firstValueFrom(this.store.select(selectFavorites))
          const superheroes = data.results?.map(e => new Superhero(e, favorites.includes(parseInt(e.id)))) ?? [];

          resolve(superheroes);
        })
        .catch(() => reject())
    })
  }

  /**
   * @description
   * Fetches the list of all superheroes
   *
   * @param page The target page
   */
  getSuperheroes(page: number): Promise<Array<Superhero>> {
    return new Promise(async (resolve, reject) => {
      const maxId = environment.totalEntries;
      const pageSize = environment.pageSize;
      const totalPages = Math.ceil(maxId / 10);
      const targetPage = Math.min(Math.max(1, page), totalPages) - 1
      const first = pageSize * targetPage;
      const nextBatch = Math.min(pageSize, Math.abs(maxId - first));

      const favorites = await firstValueFrom(this.store.select(selectFavorites))

      firstValueFrom(
        forkJoin(
          // Generating a batch of numbers
          [...Array(nextBatch)]

            // Sanitizing the numbers
            .map((_, i) => i + 1 + first)

            // Generating api calls
            .map(e => this.http.get<ISuperheroResponse>(`${environment.api.superheroes}/${e}`))

          // Mapping the values
        ).pipe(map(e => e.map(e => new Superhero(e, favorites.includes(parseInt(e.id))))))
      )
        .then(data => resolve(data))
        .catch(() => reject())
    })
  }

  //#endregion
}

