.card {
  --color-favorite: rgb(223, 93, 93);

  cursor: pointer;
  overflow: hidden;
  position: relative;

  width: 200px;
  height: 280px;
  border-radius: 8px;
  box-shadow: 0 4px 10px #0000006b;

  transition-duration: 0.2s;
  transition-property: transform;

  &__id {
    top: 10px;
    left: 10px;
    position: absolute;

    z-index: 1;
    opacity: 0.6;
    color: #ffffff;
    text-shadow: 2px 2px #000000, -2px -2px #000000;

    font-size: 46px;
    font-family: 'Bangers';
  }

  &__favorite {
    bottom: 4px;
    right: 10px;
    position: absolute;

    z-index: 1;
    opacity: 0;
    color: #ffffff;
    user-select: none;

    transition-duration: 0.2s;
    transition-property: opacity color transform;

    span {
      font-size: 30px;
    }

    &:hover {
      color: var(--color-favorite);
      transform: scale(1.1);
    }
  }

  &__content {
    position: absolute;
    width: 100%;
    bottom: 0;
    left: 0;
  }

  &__image {
    position: relative;

    width: 100%;
    height: 100%;

    transition-duration: 3s;
    transition-property: transform;
    transition-timing-function: ease-out;
  }

  &__title {
    color: #ffffff;
    background-color: #272727af;

    padding: 12px 10px;
    border-radius: 5px;

    font-size: 20px;
    font-weight: 300;
  }

  &--loading {
    animation-duration: 1.5s;
    animation-name: loadCard;
    animation-fill-mode: forwards;
    animation-timing-function: linear;
    animation-iteration-count: infinite;

    box-shadow: 0 2px 30px #00000011;

    background-size: 100% 200%;
    background-image: linear-gradient(to bottom, rgb(216, 216, 216), #ffffff, rgb(216, 216, 216));
  }

  &--favorited {
    outline: 3px solid var(--color-favorite);

    .card__favorite {
      opacity: 1;
      color: var(--color-favorite);
    }
  }

  &:hover:not(&--loading) {
    transform: scale(1.05);

    .card__image {
      transform: scale(1.2);
    }

    .card__favorite {
      opacity: 1;
    }
  }
}

@keyframes loadCard {
  from {
    background-position-y: 0%;
  }

  to {
    background-position-y: 200%;
  }
}

